$(document).ready(function(){$("a.toggle_menu").click(function(){$("#top_menu").toggle()});$(window).hashchange(function(){if(location.hash=="#menu")$("#top_menu").show();else location.hash===""&&$("#top_menu").hide()});$(".pagination").each(function(){$(this).find("select").change(function(){var a=$(this).find("option:selected").text(),b=$(this).parent().parent().find("span.link_base").text();if(a!="1")b+=a;window.location=b})})});
